import React, { useEffect, useState } from "react";
import Graph from "./Graph"; // Ensure Graph is implemented correctly
import axios from "axios";
import "../dashboard.css";

const Dashboard = () => {
  const [stats, setStats] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/stats')
      .then((response) => response.json())
      .then((data) => setStats(data))
      .catch((error) => console.error('Error fetching stats:', error));
  }, []);

  return (
    <div className="dashboard">
      <h1>Admin Dashboard</h1>
      {stats.map((movie) => (
        <div key={movie.name} className="movie-stats">
          <h2>{movie.name}</h2>
          {movie.shows.map((show) => (
            <div key={show.showtime} className="show-stats">
              <p><strong>Showtime:</strong> {show.showtime}</p>
              <p><strong>Sold Tickets:</strong> {show.soldTickets}</p>
              <p><strong>Available Tickets:</strong> {show.availableTickets}</p>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
