import React, { useEffect, useState } from "react";
import Graph from "./Graph"; // Ensure Graph component is implemented
import axios from "axios";
import "../dashboard.css";

const Dashboard = () => {
  const [totalTickets, setTotalTickets] = useState(0);
  const [soldTickets, setSoldTickets] = useState(0);
  const [userBookings, setUserBookings] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [dashboardData, setDashboardData] = useState([]);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const { data } = await axios.get('http://localhost:5000/api/dashboard');
        setDashboardData(data);
      } catch (error) {
        console.error('Error fetching dashboard data', error);
      }
    };

    fetchDashboardData();
  }, []);

  return (
    <div>
      <h1>Movie Ticket Booking Dashboard</h1>
      {dashboardData.map((movieData, index) => (
        <div key={index} className="movie-section">
          <h2>{movieData.movie}</h2>
          <p>Screen: {movieData.screen}</p>
          {movieData.showDetails.map((show, idx) => (
            <div key={idx} className="show-section">
              <p>Showtime: {show.showtime}</p>
              <p>Total Tickets: {show.totalTickets}</p>
              <p>Sold Tickets: {show.soldTickets}</p>
              <p>Available Tickets: {show.availableTickets}</p>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
