import React, { useEffect, useState } from "react";
import axios from "axios";
import "../dashboard.css";
import Graph from "./Graph"; // Ensure Graph is implemented correctly

const Dashboard = () => {
  const [totalTickets, setTotalTickets] = useState(0);
  const [soldTickets, setSoldTickets] = useState(0);
  const [userBookings, setUserBookings] = useState([]);

  // Function to fetch dashboard data
  const fetchDashboardData = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/dashboard");
      const { totalTickets, soldTickets, userBookings } = response.data;
      setTotalTickets(totalTickets || 0);
      setSoldTickets(soldTickets || 0);
      setUserBookings(userBookings || []);
    } catch (error) {
      console.error("Error fetching dashboard data:", error);
    }
  };

  // Function to book a ticket
  const bookTicket = async (ticketData) => {
    try {
      const response = await axios.put("http://localhost:5000/api/book", ticketData);
      alert(response.data.message);

      // Fetch updated dashboard data after booking a ticket
      fetchDashboardData();
    } catch (error) {
      console.error("Error booking ticket:", error);
      alert(error.response?.data?.error || "Failed to book ticket");
    }
  };

  // Fetch dashboard data when the component mounts
  useEffect(() => {
    fetchDashboardData();
  }, []);

  return (
    <div className="dashboard">
      <a className="Home" href="/">Home</a>
      <h1>Movie Ticket Booking Dashboard</h1>

      {/* Dashboard Info Section */}
      <div className="info-section">
        <div className="card">
          <h2>Total Tickets</h2>
          <p>{totalTickets}</p>
        </div>
        <div className="card">
          <h2>Sold Tickets</h2>
          <p>{soldTickets}</p>
        </div>
      </div>

      {/* User Bookings Section */}
      <div className="user-data-section">
        <h2>Users Who Booked Tickets</h2>
        {userBookings && userBookings.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Movie Booked</th>
              </tr>
            </thead>
            <tbody>
              {userBookings.map((user, index) => (
                <tr key={index}>
                  <td>{user.userName}</td>
                  <td>{user.email}</td>
                  <td>{user.movie}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No user bookings available.</p>
        )}
      </div>

      {/* Booking Section */}
      <div className="booking-section">
        <h2>Book Tickets</h2>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            const formData = new FormData(e.target);
            const ticketData = {
              userName: formData.get("userName"),
              email: formData.get("email"),
              movie: formData.get("movie"),
              screen: formData.get("screen"),
              showtime: formData.get("showtime"),
              ticketCount: parseInt(formData.get("ticketCount"), 10),
            };
            bookTicket(ticketData);
          }}
        >
          <input type="text" name="userName" placeholder="Your Name" required />
          <input type="email" name="email" placeholder="Your Email" required />
          <input type="text" name="movie" placeholder="Movie Name" required />
          <input type="text" name="screen" placeholder="Screen Number" required />
          <input type="text" name="showtime" placeholder="Showtime" required />
          <input type="number" name="ticketCount" placeholder="Tickets Count" required />
          <button type="submit">Book Tickets</button>
        </form>
      </div>

      {/* Graph Section */}
      <div className="graph-section">
        <Graph />
      </div>
    </div>
  );
};

export default Dashboard;
