import React, { useEffect, useState } from "react";
import Graph from "./Graph"; // Ensure Graph component is implemented
import axios from "axios";
import "../dashboard.css";
import BookingForm from './BookingForm'; // Import the BookingForm component

const Dashboard = () => {
  const [totalTickets, setTotalTickets] = useState(0);
  const [soldTickets, setSoldTickets] = useState(0);
  const [userBookings, setUserBookings] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .post("http://localhost:5000/api/dashboard")
      .then((response) => {
        const { totalTickets, soldTickets, userBookings } = response.data;
        setTotalTickets(totalTickets || 0);
        setSoldTickets(soldTickets || 0);
        setUserBookings(userBookings || []);
        setIsLoading(false);
      })
      .catch((error) => {
        setError('Failed to fetch dashboard data');
        setIsLoading(false);
      });
  }, []);


  return (
    <div className="dashboard">
      <a className="Home" href="/">Home</a>
      <h1>Movie Ticket Booking Dashboard</h1>

      {/* Loading Indicator */}
      {isLoading && <p>Loading dashboard data...</p>}
      {error && <p className="error">{error}</p>}

      {!isLoading && !error && (
        <>
          {/* Tickets Information Section */}
          <div className="info-section">
            <div className="card">
              <h2>Total Tickets</h2>
              <p>{totalTickets}</p>
            </div>
            <div className="card">
              <h2>Sold Tickets</h2>
              <p>{soldTickets}</p>
            </div>
          </div>

          {/* User Booking Data */}
          <div className="user-data-section">
            <h2>Users Who Booked Tickets</h2>
            {userBookings.length > 0 ? (
              <table>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Movie Booked</th>
                  </tr>
                </thead>
                <tbody>
                  {userBookings.map((user, index) => (
                    <tr key={index}>
                      <td>{user.userName}</td>
                      <td>{user.email}</td>
                      <td>{user.movie}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p>No user bookings available.</p>
            )}
          </div>

          {/* Graph Section */}
          <div className="graph-section">
            <h2>Ticket Sales Overview</h2>
            <Graph />
          </div>
        </>
      )}
    </div>
  );
};

export default Dashboard;
