// Dashboard.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import BookingForm from './BookingForm'; // Import the BookingForm component

const Dashboard = () => {
  const [totalTickets, setTotalTickets] = useState(0);
  const [soldTickets, setSoldTickets] = useState(0);
  const [userBookings, setUserBookings] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .post("http://localhost:5000/api/dashboard")
      .then((response) => {
        const { totalTickets, soldTickets, userBookings } = response.data;
        setTotalTickets(totalTickets || 0);
        setSoldTickets(soldTickets || 0);
        setUserBookings(userBookings || []);
        setIsLoading(false);
      })
      .catch((error) => {
        setError('Failed to fetch dashboard data');
        setIsLoading(false);
      });
  }, []);

  return (
    <div className="dashboard">
      <h1>Movie Ticket Booking Dashboard</h1>
      <div className="info-section">
        <div className="card">
          <h2>Total Tickets</h2>
          <p>{totalTickets}</p>
        </div>
        <div className="card">
          <h2>Sold Tickets</h2>
          <p>{soldTickets}</p>
        </div>
      </div>

      {/* Pass setTotalTickets to BookingForm */}
      <BookingForm
        setTotalTickets={setTotalTickets}
        movie="Movie Name"  // Replace with dynamic data if needed
        screen="Screen 1"   // Replace with dynamic data if needed
        showtime="12:00 PM"  // Replace with dynamic data if needed
        onClose={() => console.log("Close booking form")}  // Replace with close logic if needed
      />
    </div>
  );
};

export default Dashboard;
